function pieChart() {
		if(jQuery('#pie-chart').length>0) {
			var data = {
				labels: ['35%', '55%', '10%'],
				series: [30, 25, 15]
			  };
			  
			var options = {
				labelInterpolationFnc: function(value) {
				  return value[0]
				}
			  };
			  
			var responsiveOptions = [
				['screen and (min-width: 230px)', {
				  chartPadding: 10,
				  donut: true,
				  labelOffset: 40,
				  donutWidth: 50,
				  labelDirection: 'explode',
				  labelInterpolationFnc: function(value) {
					return value;
				  }
				}],
				['screen and (min-width: 230px)', {
				  labelOffset: 60,
				  chartPadding: 20
				}]
			];
			  
			new Chartist.Pie('#pie-chart', data, options, responsiveOptions);
    }
  }


